package day01;

public class 强制类型 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		double x  = 3.14; 
		int nx = (int)x;   //值为3
		char c = 'a';   //a
		int d = c+1;  //98
		System.out.println(nx);
		System.out.println(d);
		System.out.println(c);
		System.out.println((char)d); //b
		
	}

}
